import LockerManager from"./lockerManager.js";const lockerManager=new LockerManager({containerId:"lockerContainer",rows:5,cols:10});document.getElementById("closeUsageModal").addEventListener("click",(()=>{document.getElementById("usageHistoryModal").classList.add("hidden")})),document.getElementById("closeAssignmentModal").addEventListener("click",(()=>{document.getElementById("assignmentModal").classList.add("hidden")})),document.getElementById("userSearchInput").addEventListener("input",(e=>{const n=e.target.value.toLowerCase(),t=lockerManager.users.filter((e=>e.toLowerCase().includes(n)));lockerManager.renderUserSearchResults(t)}));